import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  const handleChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => setImagePreview(reader.result);
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('image', file);

    try {
      const res = await axios.post('http://localhost:5000/classify', formData);
      setResult(res.data);
    } catch (err) {
      console.error('Error uploading:', err);
    }
  };

  return (
    <div className="App">
      <h2>Upload Product Image</h2>

      <input type="file" onChange={handleChange} />
      <br />
      <button onClick={handleUpload}>Upload & Classify</button>

      {imagePreview && (
        <div className="image-preview-container">
          <img src={imagePreview} alt="Uploaded preview" className="circular-image" />
        </div>
      )}

      {result && (
        <div className="result-box">
          <p><strong>Type:</strong> {result.product_type}</p>
          <p><strong>Prediction Accuracy:</strong> {result.prediction_accuracy}</p>

          <div className="packaging-section">
            <h4>Internal Packaging</h4>
            <p><strong>Material:</strong> {result.packaging_suggestion.internal.material}</p>
            <p><strong>Why:</strong> {result.packaging_suggestion.internal.reason}</p>
          </div>

          <div className="packaging-section">
            <h4>External Packaging</h4>
            <p><strong>Material:</strong> {result.packaging_suggestion.external.material}</p>
            <p><strong>Why:</strong> {result.packaging_suggestion.external.reason}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
